mapper.select:
SELECT
    u_maker_id as uMakerId,
    project_id as projectId,
    project_type as projectType
FROM
    user_maker_${userEmail}
WHERE
    project_id = #{projectId}
AND
    project_type = #{projectType}

mapper.delete:
DELETE
FROM
    user_maker_${userEmail}
WHERE
    project_id = #{projectId}
AND
    project_type = #{projectType}
